import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

function Address() {

    const bookId=useParams()

    const [address,setAddress]=useState({
        bookName:'',
        bookImg:'',
        bookAuthor:'',
        bookPrice:'',
        customerName:'',
        customerMobileNo:'',
        customerAddress:''
    })

    function loadWishlist()
    {
      axios.get(`http://localhost:9090/cart/get/${bookId}`).then(res=>{setAddress(res.data)}).catch(err=>{console.log(err)})
    }
  
    useEffect(()=>
    {
      loadWishlist()
    },[])

  return (
    <div className='bg-dark pt-4'>
        <div className='bg-light container pt-3 rounded-4 w-50 p-5'>
            <h1 className='text-center mb-5'>Address Page</h1>
            <form>
                <label>Name:</label>
                <input type='text' placeholder='Enter Your Name' className='form-control mb-4'/>
                <label>Mobile Number:</label>
                <input type='text' placeholder='Enter Your Mobile Number' className='form-control mb-4'/>
                <label>Address:</label>
                <input type='text' placeholder='Enter Your Address to be Delevered' className='form-control mb-4'/>
            </form>
        </div>
    </div>
  )
}

export default Address
