import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
function Home() {

    const [books,setBooks]=useState([])

    useEffect(()=>{
        axios.get('http://localhost:9090/book/getAll')
        .then(res=>
            {
                setBooks(res.data) 
                console.log(res)
            })
        .catch(err=>
            {
                console.log(err)
            })
            },[])

  return (
    <div className='bg-dark text-light'>
        <div className='navbar bg-secondary'>
            <h3 className='navbar-brand text-dark ms-3'><b>Books Store</b></h3>
            <Link className='me-4 ms-auto btn btn-dark text-light'>Wishlist</Link>
            <Link className='btn btn-dark text-light me-4'>cart</Link>
        </div>
        <div className='mainBody text-center ms-5 mt-5'>
            {
                books.map(data=>(
                    <div className='childBody pt-3 ps-2 pe-2'>
                        <h3><b>{data.bookName}</b></h3>
                        <img src={data.bookImg} className='img mt-4'/>
                        <h4 className='mt-'><b>Author: </b>{data.bookAuthor}</h4>
                        <p><b>Description: </b>{data.bookDescription}</p>
                        <h1>Price: {data.bookPrice}/-</h1>
                        <h4>Genre: {data.bookGenre}</h4>
                    </div> 
                ))
            }
        </div>
    </div>
  )
}

export default Home
